mapper.delete:
delete on cascade
from usercomment
where tid = #{tid} and uid = #{uid} and pid = #{pid}

mapper.update:
update usercomment on cascade
    set comment = #{comment}
where tid = #{tid} and uid = #{uid} and pid = #{pid}

mapper.select:
select cid, uid, pid, comment_content
from usercomment
where cid = #{cid}
